package javaProjects;

public class Float_Double_data {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float enams_float = 12.5f;
		double enams_double = 430999999999999999.45d;
		char enams_char = 'C';
		boolean enams_boolean = false;
		
		System.out.println("This is a float data = " + enams_float);
        System.out.println("This is a double data = " + enams_double);
        System.out.println("This is a char data = " + enams_char);       
        System.out.println("This is a boolean data = " + enams_boolean);
      

	}

}
