package javaProjects;

public class Printing_Data_Types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int enams_int = 640000;
		byte enams_byte = -128;
		short enams_short = 1800;
		long enams_long = 85000000;
		
		
		System.out.println("This is a integer data = " + enams_int
				          + ".   This is a byte data = " + enams_byte
				          +".  This is a short data = " + enams_short);
		
		System.out.println("This is a long data = " + enams_long);
	}

}
